
public class Id {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5,b;
	     b=a--;
	     System.out.println("Value of a="+a);
	     System.out.println("Value of b="+b);
	     int c=5,d;
	     d=--c;
	     System.out.println("Value of c="+c);
	     System.out.println("Value of d="+d);

	}

}
